    if (window.scrollY >= window.screen.height / 25) {
      setNavbar(true);
      // setLogo(true);
    } else {
      setNavbar(false);
      // setLogo(false);
    }
  };

  window.addEventListener("scroll", changeBackground);

  return (
    <Navbar className={navbar ? "nav active" : "nav"} expand="lg">
      <Navbar.Brand href="/">
        <Fade delay="500">
          <div className={"logo"} id="logo"></div>
        </Fade>
